bookSourceComment:
@js:
var content=org.jsoup.Jsoup.parse(result).select("#TextContent").html();
  content=String(content).replace(/<script>LdgRead\(\);<\/script>/,"");
  content=String(content).replace(/theme\(\);/,"");

  var data=String(content).split("<br>"); 
  if(data.length>10){
    var newcontent="";
    for (var i=0; i <=5 ; i++) { 
      newcontent+=data[i]+"<br>";
    }
    for (i=data.length-7; i>5 ; i--) { 
      newcontent+=data[i]+"<br>";
    }
    for (i=data.length-6; i <data.length ; i++) { 
      newcontent+=data[i];
      if(i!=data.length-1){
        newcontent+="<br>";
      }
    }
    newcontent=String(newcontent).replace(/<br>/g, "");
    result=newcontent
  }else{
    result=content
 }

